/**
 * A location with latitude and longitude coordinates.
 * This is a platform-independent replacement for google.maps.LatLngLiteral
 */
export interface LatLngLiteral {
    lat: number;
    lng: number;
}
/**
 * A platform-independent interface for latitude/longitude coordinates
 * with methods to access the values. This mimics google.maps.LatLng.
 */
export interface LatLng {
    lat(): number;
    lng(): number;
}
/**
 * A platform-independent interface for an array of values with MVCArray-like methods.
 * This mimics the subset of google.maps.MVCArray that we use.
 */
export interface MVCArrayLike<T> {
    getArray(): T[];
}
/**
 * Options for buffer creation
 */
export interface BufferOptions {
    units?: 'meters' | 'kilometers' | 'miles' | 'feet';
    steps?: number;
}
